class RateLimiter {
  private readonly redis: Redis;
  private readonly window = 60; // 1 minute
  private readonly limit = 1000; // requests per window

  async isAllowed(clientId: string): Promise<boolean> {
    const key = `ratelimit:${clientId}`;
    const count = await this.redis.incr(key);

    if (count === 1) {
      await this.redis.expire(key, this.window);
    }

    return count <= this.limit;
  }

  async getRemainingQuota(clientId: string): Promise<number> {
    const key = `ratelimit:${clientId}`;
    const count = await this.redis.get(key) || '0';
    return Math.max(0, this.limit - parseInt(count));
  }
}
