```typescript
interface PaginationCursor {
  id: string;
  timestamp: string;
  encoding: 'base64' | 'plain';
}

class CursorPagination {
  private readonly defaultLimit = 25;

  async getPage(cursor: PaginationCursor, limit = this.defaultLimit): Promise<PaginatedResult> {
    const decodedCursor = this.decodeCursor(cursor);
    const query = this.buildQuery(decodedCursor, limit);
    const results = await this.executeQuery(query);

    return {
      data: results.items,
      nextCursor: this.encodeCursor(results.lastItem),
      hasMore: results.items.length === limit
    };
  }

  private buildQuery(cursor: DecodedCursor, limit: number): Query {
    return {
      text: `
        SELECT *
        FROM items
        WHERE (timestamp, id) < ($1, $2)
        ORDER BY timestamp DESC, id DESC
        LIMIT $3
      `,
      values: [cursor.timestamp, cursor.id, limit]
    };
  }
}
```

