```typescript
class SearchOptimizer {
  private readonly config = {
    timeoutMs: 5000,
    retryAttempts: 3,
    backoffMs: 1000
  };

  async executeSearch(query: SearchQuery): Promise<SearchResult> {
    return await this.withRetry(() => this.performSearch(query));
  }

  private async withRetry<T>(fn: () => Promise<T>): Promise<T> {
    let attempt = 0;
    while (attempt < this.config.retryAttempts) {
      try {
        return await fn();
      } catch (error) {
        attempt++;
        if (attempt === this.config.retryAttempts) throw error;
        await this.delay(this.getBackoffTime(attempt));
      }
    }
  }
}
```

