    announceDelay: 150,
    focusableSelectors: ['button', 'a', '[role="button"]'],
    minTouchTarget: 44
  };

  handleDynamicUpdate(content: string, priority: 'polite' | 'assertive'): void {
    const announcement = this.sanitizeAnnouncement(content);
    this.announceChange(announcement, priority);
    this.manageFocus();
  }

  private manageFocus(): void {
    const focusTarget = this.determineFocusTarget();
    if (focusTarget) {
      this.setFocus(focusTarget);
    }
  }
}
